return items.map(item => {
  const link = (item.json["Upload Spare Image"] || "").trim();

  let fileId = "";

  // open?id=xxxx
  if (link.includes("id=")) {
    fileId = link.split("id=")[1].split("&")[0].trim();
  }
  // file/d/xxxx/view
  else if (link.includes("/d/")) {
    fileId = link.split("/d/")[1].split("/")[0].trim();
  }

  return {
    json: {
      ...item.json,
      fileId,
    },
  };
});

const branches = [
  { branch: 'Vadapalani' },
  { branch: 'Adyar' },
  { branch: 'Ambattur' },
  { branch: 'Porur' },
  { branch: 'OMR' },
  { branch: 'Velachery' },
  { branch: 'Madipakkam' },
  { branch: 'Tambaram' },
  { branch: 'Guduvanchery' },
  { branch: 'Villivakkam' },
];

const newItems = [];

for (const item of $input.all()) {

  // Correct field name from input
  const spareName = item.json["Spare Name"];

  for (const b of branches) {
    newItems.push({
      json: {
        spareName: spareName,
        branch: b.branch
      },
      binary: item.binary
    });
  }
}

return newItems;

