// If branch text doesn't match anything, you can use a default folder (or null)
const defaultFolderId = null;

const newItems = [];

for (const item of $input.all()) {
  // Normalise branch name: trim, lower-case
  const rawBranch = item.json.branch || '';
  const key = rawBranch.toLowerCase().trim();

  const folderId = folderMap[key] ?? defaultFolderId;

  newItems.push({
    json: {
      ...item.json,
      branch: rawBranch,  // keep original branch name
      folderId,           // add folderId used by Drive node
    },
    binary: item.binary,  // keep edited image
  });
}

return newItems;

