const root = $json;

// 1) Get model text safely
let text =
  root?.output?.output?.[0]?.content?.[0]?.text ??
  root?.output?.[0]?.content?.[0]?.text ??
  root?.output?.[0]?.content?.[0]?.output_text ??
  root?.content?.[0]?.text ??
  root?.content?.[0]?.output_text ??
  root?.text ??
  root?.output_text;

if (!text || typeof text !== "string") {
  throw new Error("Could not find model text. Keys: " + Object.keys(root).join(", "));
}

// 2) Extract JSON even if extra text exists
function extractJsonString(s) {
  const cleaned = s.replace(/```json/gi, "").replace(/```/g, "").trim();
  const start = cleaned.indexOf("{");
  const end = cleaned.lastIndexOf("}");
  if (start === -1 || end === -1 || end <= start) return null;
  return cleaned.slice(start, end + 1).trim();
}

// 3) Parse JSON
let parsed;
try {
  parsed = JSON.parse(text);
} catch (e) {
  const extracted = extractJsonString(text);
  if (!extracted) throw new Error("Model output does not contain JSON.");
  parsed = JSON.parse(extracted);
}

// 4) Merge with current item fields (keep your original columns)
const merged = {
  ...root,   // keeps Spare Name, Branch, File Url etc if present in this item
  ...parsed, // blogTitle, blogBody, captions, hashtags
};

// IMPORTANT: In "Run Once for Each Item", return a single object
return { json: merged };

